import React from 'react';

import { SpinnerContainer, SpinnerOverlay } from './with-spinner.styles';

const WithSpinner = WrappedComponent => ({isLoading, ...otherProps}) => {

    return isLoading ? (
        <SpinnerOverlay>
            <SpinnerContainer />
        </SpinnerOverlay>
    ) : (
        <WrappedComponent {...otherProps}/> 
    )
};

export const Wrapper = Component => {
    const RC = (props) => {
        let newProps = {};
        for (const key in props) {
            if (typeof props[key] === "number") {
                newProps[key] = props[key] + 1;
            } else {
                newProps[key] = props[key];
            }
        }
        return <Component {...newProps}/>
    }
    return RC;
}

export const withWrapper = Component => props => {
    let newProps = {};
    for(const key in props) {
        if(props[key] === "number") {
            newProps[key] = props[key] + 1;
        } else {
            newProps[key] = props[key];
        }
    }
}

// const Num = ({a, b}) => <span style={{backgroundColor: 'red'}}>a + b = {a + b}</span>;
// const WrappedNum = Wrapper(Num);
// console.log(<WrappedNum a={1} b={2}/>);

export default WithSpinner;
